css:
.settings-view {
  display: -webkit-flex;
}
.settings-view .btn-default,
.settings-view .btn-group > .btn-default {
  background: #fff;
  color: #333333;
  border: 1px solid #ccc;
}
.settings-view .btn-default:hover,
.settings-view .btn-group > .btn-default:hover {
  color: #333333;
  background: #ddd;
}
.settings-view .btn {
  box-shadow: none;
}
.settings-view .text {
  color: #333333;
}
.settings-view .text-subtle {
  color: #666666;
}
.settings-view .error-message .alert {
  margin-bottom: 0;
}
.settings-view .error-message .error-details {
  background: #F7F7F9;
  border: 1px solid #E1E1E8;
}
.settings-view .editor-container {
  margin-bottom: 10px;
}
.settings-view .editor-container .editor.mini,
.settings-view .editor-container .editor.mini.is-focused {
  color: #333333;
  background-color: #fff;
}
.settings-view .editor-container .editor.mini .cursor,
.settings-view .editor-container .editor.mini.is-focused .cursor {
  border-color: #000;
}
.settings-view .editor-container .editor.mini .selection .region,
.settings-view .editor-container .editor.mini.is-focused .selection .region {
  background-color: rgba(0, 0, 0, 0.2);
}
.settings-view section .section-heading:first-child,
.settings-view .section .section-heading:first-child {
  padding-top: 10px;
  padding-bottom: 10px;
  border-bottom: 1px solid #858585;
  color: #262626;
}
.settings-view .link {
  color: #428bca;
}
.settings-view .link:hover,
.settings-view .link:focus {
  color: #2a6496;
}
.settings-view .config-menu {
  display: -webkit-flex;
  -webkit-flex-direction: column;
  box-shadow: -1px 0px 1px -1px #aeaeae inset;
  background-color: #e7e7e7;
}
.settings-view .config-menu .atom-banner {
  margin-left: 20px;
  margin-right: 20px;
  margin-top: 10px;
  margin-bottom: 10px;
  min-height: 64px;
  max-height: 64px;
  background-size: contain;
  background-position: center center;
  background-image: url(atom://settings-view/images/atom.png);
  background-repeat: no-repeat;
}
.settings-view .config-menu .icon:before {
  text-align: center;
}
.settings-view .config-menu .panels-menu {
  -webkit-flex-grow: 1;
  overflow: auto;
  height: 0;
}
.settings-view .config-menu .panels-menu .panel-menu-separator {
  border-bottom: 1px solid #ccc;
  margin: 10px;
}
.settings-view .config-menu .button-area {
  margin: 10px auto;
}
.settings-view .config-menu .nav > li > a {
  border-radius: 0;
  color: #434343;
}
.settings-view .config-menu .nav > li.active > a {
  background-color: #3c76dd;
  color: #f0f0f0;
}
.settings-view .panels {
  -webkit-flex: 1;
  -webkit-flex-flow: column;
  display: -webkit-flex;
  position: relative;
  overflow-y: auto;
  padding-left: 20px;
  background-color: #f2f2f2;
}
.settings-view .panels > div {
  -webkit-flex: 1;
}
.settings-view .settings-panel {
  padding-bottom: 10px;
}
.settings-view .settings-panel .section-body {
  padding-top: 5px;
  padding-left: 10px;
}
.settings-view .settings-panel label {
  color: #333333;
}
.settings-view .installed-package-view .package-keymap-table,
.settings-view .installed-package-view .package-grammars-table,
.settings-view .installed-package-view .package-snippets-table {
  padding-top: 10px;
  padding-left: 10px;
}
.settings-view .installed-package-view .package-keymap-table td,
.settings-view .installed-package-view .package-grammars-table td,
.settings-view .installed-package-view .package-snippets-table td {
  white-space: nowrap;
}
.settings-view .installed-package-view .package-keymap-table .snippet-body,
.settings-view .installed-package-view .package-grammars-table .snippet-body,
.settings-view .installed-package-view .package-snippets-table .snippet-body,
.settings-view .installed-package-view .package-keymap-table .snippet-prefix,
.settings-view .installed-package-view .package-grammars-table .snippet-prefix,
.settings-view .installed-package-view .package-snippets-table .snippet-prefix {
  font-family: monospace;
}
.settings-view .installed-package-view .update-message {
  padding-right: 10px;
}
.settings-view .installed-package-view .update-link:hover,
.settings-view .installed-package-view .repo-link:hover {
  cursor: pointer;
  text-decoration: underline;
}
.settings-view .packages {
  height: 100%;
  display: -webkit-flex;
  -webkit-flex-flow: column;
}
.settings-view .packages .themes-label {
  font-weight: bold;
}
.settings-view .packages .theme-chooser {
  padding-top: 20px;
}
.settings-view .packages .theme-description {
  margin-top: 5px;
  padding-bottom: 10px;
}
.settings-view .packages .package-container {
  padding-top: 10px;
}
.settings-view .packages .search-message,
.settings-view .packages .featured-message {
  margin: 10px 10px 0 10px;
}
.settings-view .packages .error-message {
  padding: 10px 10px 0 10px;
}
.settings-view .packages .editor-container {
  margin-bottom: 0;
  padding-bottom: 0;
}
.settings-view .packages .available-package-view .thumbnail {
  height: 150px;
}
.settings-view .packages .available-package-view .description {
  min-height: 40px;
  max-height: 40px;
  overflow: hidden;
}
.settings-view .packages .available-package-view .package-name {
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 100%;
  overflow: hidden;
}
.settings-view .packages .available-package-view .package-status {
  position: absolute;
  right: 15px;
  top: 5px;
}
.settings-view .packages .available-package-view .package-status.icon-cloud-download {
  color: #428bca;
}
.settings-view .packages .available-package-view .package-status.icon-check {
  color: #6CC644;
}
.settings-view .packages .available-package-view .package-status.icon-alert {
  color: #BD2C00;
}
.settings-view .btn-retry {
  margin-left: 10px;
}
.settings-view .readme {
  margin-top: 10px;
}
.settings-view .panel-heading .label {
  margin-left: 10px;
}
.settings-view .loading-area span:before {
  font-size: 1.1em;
  width: 1.1em;
  height: 1.1em;
}
.settings-view .error-view {
  margin: 10px 0 10px 0;
}
.settings-view .packages-panel .nav .badge {
  margin-left: 5px;
}
.settings-view .copy-icon {
  cursor: pointer;
  padding-right: 2px;
}
.settings-view .copy-icon:hover {
  color: #2a6496;
}
.settings-view .keybinding-panel table {
  width: 100%;
  table-layout: fixed;
}
.settings-view .keybinding-panel th {
  padding: 8px 10px;
  padding-left: 0;
}
.settings-view .keybinding-panel td {
  height: 25px;
}
.settings-view .keybinding-panel td,
.settings-view .keybinding-panel th {
  padding-left: 1%;
  padding-right: 1%;
  white-space: nowrap;
  overflow: hidden;
}
.settings-view .keybinding-panel col.keystroke {
  width: 13%;
}
.settings-view .keybinding-panel col.command {
  width: 38%;
}
.settings-view .keybinding-panel col.source {
  width: 8%;
}
.settings-view .keybinding-panel col.selector {
  width: 33%;
}
